CREATE TABLE public.edges (id uuid DEFAULT gen_random_uuid() NOT NULL, sub_project_id uuid NOT NULL, type varchar, source_node_id uuid NOT NULL, target_node_id uuid NOT NULL, data jsonb NOT NULL, created_at timestamp DEFAULT now() NOT NULL, PRIMARY KEY (id), FOREIGN KEY (sub_project_id) REFERENCES public.sub_project (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (source_node_id) REFERENCES public.nodes (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (target_node_id) REFERENCES public.nodes (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
